import React from 'react';

const Card = ({ name, age, id }) => {
  return (
    <div className='bg-light-green dib br3 pa3 ma2 grow'>
      <img alt="robots" src={'https://robohash.org/${id}?200x200'}/>
      <div>
        <h2>{name}</h2>
        <p>{age}</p>
      </div>
    </div>
  );
}

export default Card;
